const Login = () => {
  // ... same code as Signup
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const res = await axios.post('http://localhost:5000/api/auth/login', formData);  // ✅ LOGIN API
      dispatch(login(res.data.user));
      alert(`✅ Welcome back ${res.data.user.name}!`);
      navigate('/');
    } catch (err) {
      alert('❌ ' + (err.response?.data?.error || 'Login failed'));
    }
    setLoading(false);
  };

  return (
