            <div className="cart-box">
              <span className="cart-icon">üõí</span>
              {items.length > 0 && (
                <div className="cart-badge">{items.length}</div>
              )}
            </div>
          </Link>
        </div>
      </div>
    </header>
  );
};

export default Header;
