import { useSelector, useDispatch } from 'react-redux';
import { logout } from '../features/userSlice';
import { Link } from 'react-router-dom';

const Header = () => {
  const dispatch = useDispatch();
  const { isAuthenticated, user } = useSelector(state => state.user);

  return (
    <header>
      <nav>
        <Link to="/" className="logo-box">üçï FoodDelivery</Link>
        
        {isAuthenticated ? (
          <>
            <Link to="/cart" className="nav-link">üõí Cart</Link>
            <Link to="/orders" className="nav-link">üìã Orders</Link>
            <span style={{ color: 'white', marginRight: '1rem' }}>üëã {user?.name}</span>
            <button onClick={() => dispatch(logout())} className="nav-link" style={{ background: '#ef4444' }}>Logout</button>
          </>
        ) : (
          <>
            <Link to="/signup" className="nav-link">üîê Signup</Link>
            <Link to="/login" className="nav-link">Login</Link>
          </>
        )}
      </nav>
    </header>
  );
};
