import { useSelector, useDispatch } from 'react-redux';
import { removeFromCart, clearCart } from '../features/cartSlice';
import axios from 'axios';
import { useState } from 'react';

const Cart = () => {
  const { items, total } = useSelector(state => state.cart);
  const dispatch = useDispatch();
  const [loading, setLoading] = useState(false);

  const placeOrder = async () => {
    if (items.length === 0) return alert('Cart is empty!');

    setLoading(true);
    try {
      await axios.post('http://localhost:5000/api/orders', {
        userEmail: 'customer@gmail.com',
        items: items.map(item => ({
          foodId: item._id,
          name: item.name,
          price: item.price,
          quantity: item.quantity
        })),
        totalAmount: total
      });
      alert('‚úÖ Order placed successfully! Check /orders');
      dispatch(clearCart());
    } catch (err) {
      alert('‚ùå Error placing order');
    }
    setLoading(false);
  };

  return (
    <div className="container mx-auto py-8 px-4">
      <h1 className="text-4xl font-bold text-center mb-12">üõí Shopping Cart</h1>
      
      {items.length === 0 ? (
        <div className="text-center py-20">
          <p className="text-2xl text-gray-500 mb-4">Your cart is empty</p>
          <a href="/" className="bg-orange-500 text-white px-8 py-3 rounded-lg hover:bg-orange-600">
            Continue Shopping
          </a>
        </div>
      ) : (
        <>
          <div className="max-w-4xl mx-auto space-y-4 mb-8">
            {items.map(item => (
              <div key={item._id} className="flex items-center justify-between p-6 bg-white rounded-xl shadow">
                <div className="flex items-center space-x-4">
                  <img src={item.image || 'https://via.placeholder.com/100'} alt={item.name} className="w-20 h-20 object-cover rounded-lg" />
                  <div>
                    <h3 className="font-semibold">{item.name}</h3>
                    <p className="text-gray-600">‚Çπ{item.price} x {item.quantity}</p>
                  </div>
                </div>
                <div className="flex items-center space-x-4">
                  <span className="font-bold text-xl">‚Çπ{(item.price * item.quantity).toFixed(2)}</span>
                  <button
                    onClick={() => dispatch(removeFromCart(item._id))}
                    className="text-red-500 hover:text-red-700 font-semibold"
                  >
                    Remove
                  </button>
                </div>
              </div>
            ))}
          </div>

          <div className="max-w-md mx-auto bg-white p-8 rounded-xl shadow-lg">
            <div className="flex justify-between text-xl mb-6">
              <span>Total:</span>
              <span className="font-bold text-2xl text-orange-500">‚Çπ{total.toFixed(2)}</span>
            </div>
            <button
              onClick={placeOrder}
              disabled={loading}
              className="w-full bg-green-500 text-white py-4 rounded-xl text-lg font-semibold hover:bg-green-600 disabled:opacity-50"
            >
              {loading ? 'Placing Order...' : 'Place Order'}
            </button>
          </div>
        </>
      )}
    </div>
  );
};

export default Cart;
