import { useSelector, useDispatch } from 'react-redux';
import { removeFromCart, addToCart } from '../features/cartSlice';
import axios from 'axios';
import { useState } from 'react';

const Cart = () => {
  const { items, total } = useSelector(state => state.cart);
  const dispatch = useDispatch();
  const [loading, setLoading] = useState(false);

  const increaseQuantity = (item) => {
    dispatch(addToCart(item));
  };

  const decreaseQuantity = (item) => {
    if (item.quantity > 1) {
      dispatch(addToCart(item)); // Redux me quantity already handle hoti hai
    } else {
      dispatch(removeFromCart(item._id));
    }
  };

  const placeOrder = async () => {
    if (items.length === 0) return alert('Cart is empty!');

    setLoading(true);
    try {
      await axios.post('http://localhost:5000/api/orders', {
        userEmail: 'customer@gmail.com',
        items: items.map(item => ({
          foodId: item._id,
          name: item.name,
          price: item.price,
          quantity: item.quantity || 1
        })),
        totalAmount: total
      });
      alert('‚úÖ Order placed successfully! Check /orders');
      dispatch({ type: 'cart/clearCart' });
    } catch (err) {
      alert('‚ùå Error placing order');
    }
    setLoading(false);
  };

  if (items.length === 0) {
    return (
      <div className="container mx-auto py-8 px-4">
        <h1 className="text-4xl font-bold text-center mb-12">üõí Shopping Cart</h1>
        <div className="text-center py-20">
          <p className="text-2xl text-gray-500 mb-4">Your cart is empty</p>
          <a href="/" className="bg-orange-500 text-white px-8 py-3 rounded-lg hover:bg-orange-600">
            Continue Shopping
          </a>
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto py-8 px-4">
      <h1 className="text-4xl font-bold text-center mb-12">üõí Shopping Cart ({items.length} items)</h1>
      
      {/* Cart Items List */}
      <div className="max-w-4xl mx-auto space-y-6 mb-8">
        {items.map((item) => (
          <div key={item._id} className="flex items-center justify-between p-6 bg-white rounded-xl shadow-lg hover:shadow-xl transition-all">
            {/* Image + Details */}
            <div className="flex items-center space-x-4 flex-1">
              <img 
                src={item.image || 'https://via.placeholder.com/100?text=Food'} 
                alt={item.name} 
                className="w-20 h-20 object-cover rounded-lg shadow-md"
              />
              <div>
                <h3 className="font-bold text-lg">{item.name}</h3>
                <p className="text-gray-600">‚Çπ{item.price}</p>
              </div>
            </div>

            {/* Quantity Controls */}
            <div className="flex items-center space-x-3">
              <button
                onClick={() => decreaseQuantity(item)}
                className="w-10 h-10 bg-gray-200 rounded-full flex items-center justify-center hover:bg-gray-300 transition-colors"
              >
                -
              </button>
              <span className="font-bold text-lg w-8 text-center">{item.quantity || 1}</span>
              <button
                onClick={() => increaseQuantity(item)}
                className="w-10 h-10 bg-orange-500 text-white rounded-full flex items-center justify-center hover:bg-orange-600 transition-colors"
              >
                +
              </button>
            </div>

            {/* Price + Remove */}
            <div className="flex flex-col items-end space-y-2">
              <span className="font-bold text-xl text-orange-500">
                ‚Çπ{(item.price * (item.quantity || 1)).toFixed(2)}
              </span>
              <button
                onClick={() => dispatch(removeFromCart(item._id))}
                className="text-red-500 hover:text-red-700 font-semibold text-sm px-3 py-1 rounded hover:bg-red-50 transition-colors"
              >
                Remove
              </button>
            </div>
          </div>
        ))}
      </div>

      {/* Total + Place Order */}
      <div className="max-w-md mx-auto bg-white p-8 rounded-xl shadow-xl border-t-4 border-orange-500">
        <div className="space-y-4 text-2xl">
          <div className="flex justify-between font-bold">
            <span>Total:</span>
            <span className="text-3xl text-orange-500">‚Çπ{total.toFixed(2)}</span>
          </div>
          <button
            onClick={placeOrder}
            disabled={loading || items.length === 0}
            className="w-full bg-green-500 text-white py-4 rounded-xl text-xl font-bold hover:bg-green-600 disabled:opacity-50 disabled:cursor-not-allowed shadow-lg transform hover:-translate-y-1 transition-all duration-200"
          >
            {loading ? 'üîÑ Placing Order...' : `‚úÖ Place Order (‚Çπ${total.toFixed(2)})`}
          </button>
        </div>
      </div>
    </div>
  );
};

export default Cart;
