import { Link } from 'react-router-dom';
import { useSelector } from 'react-redux';

const Header = () => {
  const { total, items } = useSelector(state => state.cart);

  return (
    <header className="bg-orange-500 text-white p-4 shadow-lg">
      <div className="container mx-auto flex justify-between items-center">
        <Link to="/" className="text-2xl font-bold">üçï FoodHub</Link>
        <div className="flex items-center space-x-4">
          <Link to="/orders" className="hover:underline">Orders</Link>
          <Link to="/cart" className="relative">
            Cart ({items.length})
            <span className="absolute -top-2 -right-2 bg-red-600 text-xs rounded-full px-1.5 py-0.5">
              ‚Çπ{total.toFixed(2)}
            </span>
          </Link>
        </div>
      </div>
    </header>
  );
};

export default Header;
